import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import Second from './Second';

function App() {
  let [getBalance, setBalance] = useState(0)
  let [getHistory, setHistory] = useState([
    {balance: 1000, type: "income",description: ""},
    {balance: 1000, type: "income",description: ""},
    {balance: 1000, type: "income",description: ""},
    {balance: 1000, type: "outcome",description: ""},
    {balance: 1000, type: "outcome",description: ""},
    {balance: 1000, type: "outcome",description: ""},
  ])

  const getOutcomeBalance = () =>{
    let outcomebalance = 0
    getHistory.forEach((history) =>{
      if(history.type === "outcome"){
        outcomebalance += history.balance
      }
    }) 
    console.log(outcomebalance)
  }

  return (
    <div className="App">
      <h1>Expense Tracker</h1>
      <h3>Your Balance</h3>
      <h2>IDR {getBalance}</h2>
      <div className='income'>
        <h4>INCOME</h4>
        <span className='green-accent' onClick={getOutcomeBalance}>IDR </span>
      </div>
      <div className='outcome'>
        <h4>OUTCOME</h4>
      </div>
    </div>
  );
}

export default App;
