import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import Second from './Second';

function App() {
  let [getInputText, setInputText] = useState('')
  let [getInputAmount, setInputAmount] = useState('')
  let [getHistory, setHistory] = useState([
    {balance: "+1000", type: "income",description: "Cek"},
    {balance: "+1000", type: "income",description: "Cek"},
    {balance: "-1000", type: "outcome",description: "Cek"}
  ])
  const addNewTransaction = (event) =>{
    if(!(getInputAmount === '' && getInputText === '')){
      let isIncome = "income"
      if (parseInt(getInputAmount) < 0) {
          isIncome = "outcome"
      }
      const newTransaction = {
        balance: getInputAmount,
        type: isIncome,
        description: getInputText
      }
    }

  }
  const handleInputText = (event) =>{
    setInputText(event.target.value)
    // console.log(getInputTodo)
  }
  const handleInputAmount = (event) =>{
    setInputAmount(event.target.value)
    // console.log(getInputTodo)
  }
  const getOutcomeBalance = () =>{
    let outcomebalance = 0
    getHistory.forEach((history) =>{
      if(history.type === "outcome"){
        outcomebalance += parseInt(history.balance)
      }
    }) 
    return outcomebalance
  }
  const getIncomeBalance = () =>{
    let incomebalance = 0
    getHistory.forEach((history) =>{
      if(history.type === "income"){
        incomebalance += parseInt(history.balance)
      }
    }) 
    return incomebalance
  }

  return (
    <div className="App">
      <h1>Expense Tracker</h1>
      <h3>Your Balance</h3>
      <h2>IDR {getIncomeBalance() + getOutcomeBalance()}</h2>
      <div className='outincontainer'>
        <div className='income'>
          <h4>INCOME</h4>
          <span className='green-accent'>IDR {getIncomeBalance()}</span>
        </div>
        <div className='outcome'>
          <h4>OUTCOME</h4>
          <span className='red-accent'>IDR {getOutcomeBalance()}</span>
        </div>
      </div>
      <div className='history'>
        <h3>History</h3>
        <ul>
            {
              getHistory.map((history)=>{
                  return <li><p className='leftp'>{history.description}</p><p className='rightp'>{history.balance}</p></li>
              })
            }

        </ul>
      </div>

      <div className='addnewtransaction'>
        <h3>Add new transaction</h3>
        <p>Text</p>
        <input value={getInputText} onChange={handleInputText} type='text'/>
        <p>Amount</p>
        <p>(negative -expense, positif +expense)</p>
        <input value={getInputAmount} onChange={handleInputAmount} type='text'/>
        <button onClick={addNewTransaction}>Add Transaction</button>
      </div>
    </div>
  );
}

export default App;
