import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  let [getJudul, setJudul] = useState("Halo Dunia")

  const handleOnClick = (event) => {
    if(getJudul === "halo"){
      setJudul('Halo Dunia')
    }else{
      setJudul('halo')
    }
  }

  return (
    <div className="App">
      <h1>{getJudul}</h1>
      <button onClick={handleOnClick}>Ganti</button>
    </div>
  );
}

export default App;
